import {Link} from 'react-router-dom'

function TVLTable(props){
    const {name} = props;
    return(
        <div class="infoitem">
            <h3>
                Total Value Locked
            </h3>
            <table>
                <tr>
                    <td id="head">in USD</td>
                    <td id="data">$ xxx.x M</td>
                    <td id="data">+ x.x%</td>
                </tr>
                <tr>
                    <td id="head">in ETH</td>
                    <td id="data">xxx.x K ETH</td>
                    <td id="data">+ x.x%</td>
                </tr>
                <tr>
                    <td id="head">in BTC</td>
                    <td id="data">xxx.x K BTC</td>
                    <td id="data">+ x.x%</td>
                </tr>
                <tr>
                    <td id="head">ETH Locked</td>
                    <td id="data">xxx.x K ETH</td>
                    <td id="data">+ x.x K ETH</td>
                </tr>
            </table>
        </div>
    );
}

function CreditComponent(props){
    const {name} = props;
    return(
        <div class="infoitem">
            <h3>
                Credit
            </h3>
            <table>
                <tr>
                    <td id="head">Project Launch Date</td>
                    <td id="data">xx/xx/xxxx</td>
                </tr>
                <tr>
                    <td id="head">Developer</td>
                    <td id="data">xxxxxxxxxx</td>
                </tr>
                <tr>
                    <td id="head">Last Exploit</td>
                    <td id="data">xx/xx/xxxx</td>
                </tr>
            </table>
        </div>
    );
}

function ChartComponent(props){
    const {name} = props;
    return(
        <div class="infoitem">
            <h3>
                Chart
            </h3>
            <p>WIP</p>
        </div>
    );
}

function DescriptionComponent(props){
    const {name} = props;
    return(
        <div class="infoitem">
            <h3>{name}'s Description</h3>
            <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.</p>
        </div>
    );
}

function AnalysisComponent(props){
    const {name} = props;
    return(
        <div class="infoitem">
            <h3>Analysis</h3>
            <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.</p>
        </div>
    );
}

function ExploitCaseComponent(props){
    const {name} = props;
    return(
        <div class="infoitem">
            <h3>Exploited Case</h3>
            <p>19 October 2021</p>
            <p><t></t>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.</p>
        </div>
    );
}

function InfoPage(props){
    const name=props.match.params.name;
    return(
        <div>
            <div id="title">
                <Link to="/" style={{textDecoration: 'none'}}><h1>SCAT</h1></Link>
            </div>
            <div id="infotitle">
                <h2>{name}</h2>
                <h3>Key Statistic</h3>
            </div>
            <div class="infopage">
                <div class="infolist-a">
                    <TVLTable name={name} />
                    <CreditComponent name={name} />
                </div>
                <div class="infolist-b">
                    <ChartComponent name={name} />
                    <DescriptionComponent name={name} />
                    <AnalysisComponent name={name} />
                </div>
            </div>
        </div>
    );
}

export default InfoPage;

